mapper.select:
select id,posts.user_id as userId,common_number as commonNumber,tb_user.avatar_url as avatarUrl,tb_user.username as username,
       favorite_number as favoriteNumber,description,posts.create_time as createTime from posts INNER JOIN tb_user
        on posts.user_id = tb_user.user_id where id = #{id};

select posts.id,tb_user.user_id as userId,tb_user.avatar_url as avatarUrl,common_number as commonNumber,tb_user.username,
       favorite_number as favoriteNumber,description,posts.create_time as createTime from posts left join tb_user on
    posts.user_id = tb_user.user_id;

select posts.id,tb_user.user_id as userId,tb_user.avatar_url as avatarUrl,common_number as commonNumber,tb_user.username,
       favorite_number as favoriteNumber,description,posts.create_time as createTime from posts left join tb_user on
    posts.user_id = tb_user.user_id where posts.user_id = #{userId};
